import noteContext from "./noteContext";
import { useState } from "react";

const NoteState = (props) => {
  const host = "http://localhost:5000";
  const notesInitial = [];
  const [notes, setNotes] = useState(notesInitial);


  // Add a Note
  const addNote = async (title, description, tag) => {
    const response = await fetch(`${host}/api/notes/addnotes `, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjIwZGZhODBlOGU5ZTE5YmVhZDMxNmJjIn0sImlhdCI6MTY0NTA5NzIyNX0.yEfB6z2AX1qDF5VCZZR614HPw3WqHTLbmVr5M9LYxvQ",
      },
      body: JSON.stringify({ title, description, tag }),
    });
    const note = {
      _id: "6223612df7a950ecdc1c2ca6",
      user: "620dfa80e8e9e19bead316bc",
      title: title,
      description: description,
      tag: tag,
      date: "2022-03-05T13:10:05.576Z",
      __v: 0,
    };
    setNotes(notes.concat(note));
  };

  // Delete a Note
  const deleteNote = (id) => {
    console.log("Deleting Note with " + id);
    const newNotes = notes.filter((note) => {
      return note._id !== id;
    });
    setNotes(newNotes);
  };

  // Edit a Note
  const editNote = async (id, title, description, tag) => {
    // API Call
    const response = await fetch(`${host}/api/notes/updatenote/${id}`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjIwZGZhODBlOGU5ZTE5YmVhZDMxNmJjIn0sImlhdCI6MTY0NTA5NzIyNX0.yEfB6z2AX1qDF5VCZZR614HPw3WqHTLbmVr5M9LYxvQ",
      },
      body: JSON.stringify({ title, description, tag }),
    });
    const json = response.json();
    for (let index = 0; index < notes.length; index++) {
      // Logic to edit in client
      const element = notes[index];
      if (element._id === id) {
        element.title = title;
        element.description = description;
        element.tag = tag;
      }
    }
  };

  return (
    <noteContext.Provider
      value={{ notes, setNotes, addNote, editNote, deleteNote }}
    >
      {props.children}
    </noteContext.Provider>
  );
};

export default NoteState;
