import noteContext from "./noteContext";
import { useState } from "react";

const NoteState = (props) => {
  const notesInitial = [
    {
      _id: "621e045da9554ac3a47ffec0",
      user: "620dfa80e8e9e19bead316bc",
      title: "My First Note",
      description: "Hello Everyone, this is my first note",
      tag: "Personal",
      date: "2022-03-01T11:32:45.927Z",
      __v: 0,
    },
    {
      _id: "6223612df7a950ecdc1c2ca6",
      user: "620dfa80e8e9e19bead316bc",
      title: "My Second Note",
      description: "Hello Everyone, this is my second note",
      tag: "Personal",
      date: "2022-03-05T13:10:05.576Z",
      __v: 0,
    },
  ];
  const [notes, setNotes] = useState(notesInitial);

  //Add a Note
  const addNote = (title, description, tag) => {
    let note = {
      _id: "6223612df7a950ecdc1c2ca6",
      user: "620dfa80e8e9e19bead316bc",
      title: title,
      description: description,
      tag: tag,
      date: "2022-03-05T13:10:05.576Z",
      __v: 0,
    };
    setNotes(notes.concat(note));
  };

  //Delete a Note
  const deleteNote = (id) => {
    console.log("Deleting Note with " + id);
    const newNotes = notes.filter((note) => {
      return note._id !== id;
    });
    setNotes(newNotes);
  };

  //Edit a Note
  const editNote = (id, title, description, tag) => {
    for (let index = 0; index < notes.length; index++) {
      const element = notes[index];
      if(element._id === id) {
        element.title = title;
        element.des = title;
        element.title = title;
      }
    }
  };

  return (
    <noteContext.Provider
      value={{ notes, setNotes, addNote, editNote, deleteNote }}
    >
      {props.children}
    </noteContext.Provider>
  );
};

export default NoteState;
